// Import the functions you need from the SDKs you need
import { initializeApp } from "firebase/app";
import { getFirestore } from "firebase/firestore";
import { getAuth, GoogleAuthProvider, onAuthStateChanged } from "firebase/auth";
// TODO: Add SDKs for Firebase products that you want to use
// https://firebase.google.com/docs/web/setup#available-libraries
import {
  signInWithPopup,
  signInWithEmailAndPassword,
  createUserWithEmailAndPassword,
  sendPasswordResetEmail,
  signOut,
} from "firebase/auth";
import {
  query,
  getDocs,
  collection,
  where,
  addDoc,
  setDoc,
  doc,
  updateDoc,
  getDoc,
} from "firebase/firestore";
import { useEffect } from "react";

// Your web app's Firebase configuration
const firebaseConfig = {
  apiKey: "AIzaSyCWHGDYvn_n-wwC9ZOskp1USNRquFWdw4U",
  authDomain: "heartdiseasediagnosisml.firebaseapp.com",
  projectId: "heartdiseasediagnosisml",
  storageBucket: "heartdiseasediagnosisml.appspot.com",
  messagingSenderId: "48045985241",
  appId: "1:48045985241:web:e3ad3f6c58d209ba47ddc8",
};
// Initialize Firebase
export const app = initializeApp(firebaseConfig);

export const db = getFirestore(app);
export const auth = getAuth(app);
export const provider = new GoogleAuthProvider();

export const logInWithEmailAndPassword = async (email, password) => {
  try {
    await signInWithEmailAndPassword(auth, email, password);
  } catch (err) {
    console.error(err);
    alert(err.message);
  }
};
export const registerWithEmailAndPassword = async (
  name,
  email,
  password,
  phone,
  address,
  language,
  gender
) => {
  try {
    const res = await createUserWithEmailAndPassword(auth, email, password);
    const user = res.user;
    await setDoc(doc(db, "users", user.uid), {
      uid: user.uid,
      name,
      email,
      phone,
      address,
      language,
      gender,
    });
  } catch (err) {
    console.error(err);
    alert(err.message);
  }
};

export const logout = () => {
  signOut(auth);
};

const delay = (ms) => new Promise((res) => setTimeout(res, ms));

export const getData = async () => {
  const auth2 = getAuth();
  const user = auth2.currentUser;
  const uid = user.uid;
  const docRef = doc(db, "users", uid);
  const docSnap = getDoc(docRef);
  const address = docSnap.data().address;
  const name = docSnap.data().name;
  const number = docSnap.data().phone;
  const gender = docSnap.data().gender;
  const email = docSnap.data().email;
  const language = docSnap.data().language;
  const data = [address, name, number, gender, email, language];
