import React, { useEffect, useState } from "react";
import "../styles/dashboard.min.css";
import "../styles/daterangepicker.css";
import "../styles/ion.rangeSlider.min.css";
import "../styles/jquery.scrollbar.css";
import "../styles/select2.css";
import "../styles/swiper.css";
import Avatar from "../images/IMG_0793.jpg";
import Results from "../images/icons/icons-64-white/flask.png";
import Stethoscope from "../images/icons/icons-64-white/stethoscope.png";
import Doctor from "../images/icons/icons-64-white/doctor.png";
import { Link } from "react-router-dom";
import { auth, db } from "../firebase-config";
import {
  query,
  getDocs,
  collection,
  where,
  addDoc,
  setDoc,
  doc,
  updateDoc,
  getDoc,
} from "firebase/firestore";
import { useAuthState } from "react-firebase-hooks/auth";

const getCollectionData = async () => {
  var documentList = [];
  var documentResult = [];
  const querySnapshot = await getDocs(
    collection(db, "previous-patient-diagnoses")
  );
  querySnapshot.forEach((doc) => {
    documentList.push(doc.id);
    documentResult.push(doc.data());
  });

  console.log(documentList);
  console.log(documentResult);

  var n = documentList.length()

  const layout = Array.apply(null, { length: n }).map((e, i) => (
    <span key={i}>
      {documentResult}
    </span>
  ));

  return <div>{layout}</div>;
};

function PatientDataset() {
  const [user, loading, error] = useAuthState(auth); //USE THIS TO LOAD IN USER STATE

  useEffect(() => {
    if (loading) return;
  }, [user, loading]);

  if (user) {
    getCollectionData();
  }
}

export default PatientDataset;
